import React, {Component} from 'react';
import TrackerReact from 'meteor/ultimatejs:tracker-react';
import ReactCSSTransitionGroup from 'react-addons-css-transition-group';

export default class NavButton extends Component {

	render() {
		var navOn = Session.get('navSideBarOn');
		console.log('Session is', navOn);
		if (navOn) {
			menuButtonClass = "";
		} else {
			menuButtonClass = "menu-button-off";
		}

		return (
			<ReactCSSTransitionGroup
				component="div"
				transitionName="route"
				transitionEnterTimeout={600}
				transitionAppearTimeout={600}
				transitionLeaveTimeout={400}
				transitionAppear={true}>

	        {/* Nav Menu Hamburger Icon */}
		        <div id="menu-button" className={menuButtonClass} onClick={Session.set('navSideBarOn', !navOn)}>
		          <div id="line1" className="hamburger-lines1"></div>
		          <div id="line2" className="hamburger-lines2"></div>
		          <div id="line3" className="hamburger-lines3"></div>
		          <div id="line4" className="hamburger-lines4"></div>
		        </div>

			</ReactCSSTransitionGroup>
		)
	}
}
